import ProductCard from './ProductCard';
import { useState } from 'react';
import '../App.css'

function ProductCollection(props) {
    const [productsPerPage, setProductsPerPage] = useState(3);
    const [currentPage, setCurrentPage] = useState(1);
    const [searchBy, setSearchBy] = useState('all');
    const [searchTerm, setSearchTerm] = useState('');

    const handleLoadMore = () => {
        setCurrentPage(currentPage + 1);
    };

    const handleGoBack = () => {
        setCurrentPage(currentPage - 1);
    };

    const clearSearch = () => {
        setSearchBy('all');
        setSearchTerm('');
    };

    const { allProducts, filterProducts } = props;

    const startIndex = (currentPage - 1) * productsPerPage;
    const endIndex = startIndex + productsPerPage;

    const productsToDisplay =
        (searchBy === 'all' && searchTerm === '')
            ? allProducts.slice(startIndex, endIndex)
            : allProducts.filter(product => (
                (searchBy === 'all' || searchBy === 'category' && product.category === searchTerm ||
                 searchBy === 'name' && product.name.toLowerCase().includes(searchTerm.toLowerCase()))
            )).slice(startIndex, endIndex);

    const canLoadMore = endIndex < (searchBy === 'all' && searchTerm === '' ? allProducts.length : filterProducts(searchTerm).length);
    const canGoBack = currentPage > 1;

    return (
        <div className='overaldiv'>
            <div className='searchBar'>
                <select value={searchBy} onChange={(e) => setSearchBy(e.target.value)}>
                    <option value='all'>All</option>
                    <option value='category'>Category</option>
                    <option value='name'>Name</option>
                </select>
                <input
                    type='text'
                    placeholder='Search'
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
                <button className='pagination-btn' onClick={clearSearch}>
                    Clear Search
                </button>
            </div>
            <div className='productCollection row align-items-stretch'>
                {productsToDisplay.map((product) => (
                    <ProductCard
                        key={product.id}
                        id={product.id}
                        product={product}
                        addToBasket={props.addToBasket}
                        basket_id={props.basket_id}
                        customerBasket={props.customerBasket}
                        history={props.history}
                        current_user={props.current_user}
                    />
                ))}
            </div>
            <div className='pagination'>
                {canGoBack && (
                    <button className='pagination-btn' onClick={handleGoBack}>
                        Prev
                    </button>
                )}
                {canLoadMore && (
                    <button className='pagination-btn' onClick={handleLoadMore}>
                        Load More
                    </button>
                )}
            </div>
        </div>
    );
}

export default ProductCollection;
