import ProductCard from './ProductCard';
import { useState } from 'react';
import '../App.css';

function ProductCollection(props) {
  const [productsPerPage, setProductsPerPage] = useState(3);
  const [currentPage, setCurrentPage] = useState(1);
  const [filterCategory, setFilterCategory] = useState('');

  const handleLoadMore = () => {
    setCurrentPage(currentPage + 1);
  };

  const handleGoBack = () => {
    setCurrentPage(currentPage - 1);
  };

  const { allProducts, filterProducts } = props;

  const startIndex = (currentPage - 1) * productsPerPage;
  const endIndex = startIndex + productsPerPage;

  const filteredProducts = filterCategory === '' ? allProducts : filterProducts(filterCategory);

  const productsToDisplay = filteredProducts.slice(startIndex, endIndex);

  const canLoadMore = endIndex < filteredProducts.length;
  const canGoBack = currentPage > 1;

  const handleCategoryChange = (event) => {
    setFilterCategory(event.target.value);
    setCurrentPage(1); // Reset the page when the category is changed
  };

  const categories = ['Category A', 'Category B', 'Category C'];

  return (
    <div className='overaldiv'>
      <div>
        <label htmlFor='category-select'>Filter by category:</label>
        <select id='category-select' value={filterCategory} onChange={handleCategoryChange}>
          <option value=''>All categories</option>
          {categories.map((category) => (
            <option key={category} value={category}>
              {category}
            </option>
          ))}
        </select>
      </div>
      <div className='productCollection row align-items-stretch'>
        {productsToDisplay.map((product) => (
          <ProductCard
            key={product.id}
            id={product.id}
            product={product}
            addToBasket={props.addToBasket}
            basket_id={props.basket_id}
            customerBasket={props.customerBasket}
            history={props.history}
            current_user={props.current_user}
          />
        ))}
      </div>
      <div style={{}} className='pagination'>
        {canGoBack && (
          <button className='pagination-btn' onClick={handleGoBack}>
            Prev
          </button>
        )}
        {canLoadMore && (
          <button className='pagination-btn' onClick={handleLoadMore}>
            Load More
          </button>
        )}
      </div>
    </div>
  );
}

export default ProductCollection;
