import React, { Component } from "react";
import { Link } from "react-router-dom";

import API from "../data/API";

class Signup extends Component {
  state = {
    email: "",
    first_name: "",
    last_name: "",
    address: "",
    phone: "",
    password: "",
    user_type: "",
    errors: {
      email: "",
      first_name: "",
      last_name: "",
      address: "",
      phone: "",
      password: "",
      user_type: "",
    },
  };

  handleSubmit = (e) => {
    e.preventDefault();
    const { errors, ...data } = this.state;

    if (Object.values(errors).some((error) => error)) {
      alert("Please fix the errors in the form before submitting");
      return;
    }

    API.signup(data).then((data) => {
      if (data.error) {
        alert("Registration failed. Please try again.");
      } else {
        this.props.signup(this.state.email, this.state.user_type);
        this.props.history.push("/signin");
      }
    });
  };

  handleChange = (e) => {
    e.preventDefault();
    const { name, value } = e.target;
    let errors = { ...this.state.errors };

    switch (name) {
      case "email":
        errors.email = !/\S+@\S+\.\S+/.test(value)
          ? "Invalid email address"
          : "";
        break;
      case "first_name":
        errors.first_name =
          value.length < 2
            ? "First name must be at least 2 characters long"
            : "";
        break;
      case "last_name":
        errors.last_name =
          value.length < 2
            ? "Last name must be at least 2 characters long"
            : "";
        break;
      case "address":
        errors.address =
          value.length < 10
            ? "Address must be at least 10 characters long"
            : "";
        break;
      case "phone":
        errors.phone = !/^[0-9]{10}$/.test(value)
          ? "Phone number must be 10 digits long"
          : "";
        break;
      case "password":
        errors.password =
          value.length < 8 ? "Password must be at least 8 characters long" : "";
        break;
      case "user_type":
        errors.user_type = !value ? "Please select a user type" : "";
        break;
      default:
        break;
    }

    this.setState({ errors, [name]: value });
  };

  handleUserType = (e) => {
    e.preventDefault();
    this.setState({ user_type: e.target.value });
  };

  render() {
    return (
      // <!-- Default form login -->
      <div id="signup-form-container">
        <div id="signup-image"></div>
        <form id="signup-form" className="text-center ">
          <h2>NEW USER</h2>
          <br></br>
          <fieldset>
            <div>
              <select
                className="form-control mb-4"
                id="select-user-type"
                onChange={this.handleUserType}
                value={this.state.value}
              >
                <option value=""> -- Select an option -- </option>
                <option value="farmer">I am a farmer</option>
                <option value="customer">I am a Customer</option>
              </select>
            </div>
          </fieldset>

          <br></br>

          <fieldset>
            <input
              name="first_name"
              id="defaultSignup"
              onChange={this.handleChange}
              value={this.state.first_name}
              className="form-control mb-4"
              placeholder="first name"
            />
            {this.state.errors.first_name && (
              <div className="invalid-feedback">
                {this.state.errors.first_name}
              </div>
            )}
            <input
              name="last_name"
              id="defaultSignup"
              onChange={this.handleChange}
              value={this.state.last_name}
              className="form-control mb-4"
              placeholder="last name"
            />
            {this.state.errors.last_name && (
              <div className="invalid-feedback">
                {this.state.errors.last_name}
              </div>
            )}
            <input
              name="address"
              id="defaultSignup"
              onChange={this.handleChange}
              value={this.state.address}
              className="form-control mb-4"
              placeholder="address"
            />
            {this.state.errors.address && (
              <div className="invalid-feedback">
                {this.state.errors.address}
              </div>
            )}

            <input
              name="email"
              id="defaultSignup"
              onChange={this.handleChange}
              value={this.state.email}
              className="form-control mb-4"
              placeholder="email"
            />
             {this.state.errors.email && (
              <div className="invalid-feedback">
                {this.state.errors.email}
              </div>
            )}
            <input
              name="phone"
              id="defaultSignup"
              onChange={this.handleChange}
              value={this.state.phone}
              className="form-control mb-4"
              placeholder="phone"
            />

            <input
              name="password"
              type="password"
              id="defaultSignup"
              onChange={this.handleChange}
              value={this.state.password}
              className="form-control mb-4"
              placeholder="Password"
            />
          </fieldset>

          <Link to="/signin">
            <button
              className="btn btn-block btn-outline-green btn-lg"
              id="signup-btn"
              onClick={(e) => this.handleSubmit(e)}
              type="submit"
            >
              Sign me up!
            </button>
          </Link>
          <Link to="/signin">
            <p id="signup-question">Already have an Account?</p>
          </Link>
        </form>
      </div>
    );
  }
}

export default Signup;
