import React, { Component } from 'react';
import ProductCard from './ProductCard';

class ProductCollection extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentPage: 1,
      productsPerPage: 3,
    };
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick() {
    this.setState({
      currentPage: this.state.currentPage + 1,
    });
  }

  render() {
    const { allProducts, filterCategory, filterProducts } = this.props;
    const { currentPage, productsPerPage } = this.state;
    const indexOfLastProduct = currentPage * productsPerPage;
    const indexOfFirstProduct = indexOfLastProduct - productsPerPage;
    let currentProducts = [];
    if (filterCategory === '') {
      currentProducts = allProducts.slice(
        indexOfFirstProduct,
        indexOfLastProduct
      );
    } else {
      currentProducts = filterProducts(filterCategory).slice(
        indexOfFirstProduct,
        indexOfLastProduct
      );
    }
    const renderProducts = currentProducts.map((product) => (
      <ProductCard
        key={product.id}
        id={product.id}
        product={product}
        addToBasket={this.props.addToBasket}
        basket_id={this.props.basket_id}
        customerBasket={this.props.customerBasket}
        history={this.props.history}
        current_user={this.props.current_user}
      />
    ));
    return (
      <div className="productCollection row align-items-stretch">
        {renderProducts}
        <div className="pagination">
          <button
            onClick={() => {
              this.handleClick();
            }}
          >
            Load more
          </button>
        </div>
      </div>
    );
  }
}

export default ProductCollection;
