import './../App.css';
import React, { Component } from 'react';

class ResourcesPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tips: [
        "Use organic fertilizers to avoid soil degradation and water pollution.",
        "Crop rotation is a useful technique to prevent soil depletion and increase crop yields.",
        "Plant cover crops to improve soil health and reduce erosion.",
        "Choose crops that are suitable for your soil and climate to minimize water and fertilizer use.",
        "Practice integrated pest management (IPM) to control pests without harming the environment.",
        "Maintain proper irrigation to ensure efficient use of water and prevent waterlogging and salinization.",
        "Apply mulch to reduce soil temperature, retain moisture, and suppress weeds.",
        "Composting is a great way to recycle organic waste and enrich soil fertility.",
        "Use crop residues for livestock feed, fuel, or compost instead of burning them.",
        "Protect natural habitats and wildlife by practicing sustainable land use.",
      ],
      currentTip: "",
    };
  }

  componentDidMount() {
    this.generateTip();
  }

  generateTip = () => {
    const { tips } = this.state;
    const randomIndex = Math.floor(Math.random() * tips.length);
    const currentTip = tips[randomIndex];
    this.setState({ currentTip });
  };

  render() {
    const { currentTip } = this.state;
    return (
      <div>
        <p>Here are some resources you can use to learn about agriculture:</p>
        <div className="tip-generator">
          <p>{currentTip}</p>
          <button onClick={this.generateTip}>Generate Tip</button>
        </div>
      </div>
    );
  }
}

export default ResourcesPage;
