import React from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import API from '../data/API';

<div className="video-container">
  <video autoPlay loop muted>
    <source src="path/to/video.mp4" type="video/mp4" />
  </video>
</div>


const validationSchema = Yup.object().shape({
  email: Yup.string()
    .email('Invalid email')
    .required('Email is required'),
  password: Yup.string()
    .min(6, 'Password must be at least 6 characters')
    .required('Password is required')
});

const initialValues = {
  email: '',
  password: '',
};

const Signin = ({ signin }) => {

  const handleSubmit = (values, { setSubmitting }) => {
    API.signin(values)
      .then(data => {
        setSubmitting(false);
        if (data.error){
          alert("Email address not found");
        }
        else {
          // user is authenticated! 
          signin(values.email, data.user, data.token);
        }
      });
  };

  return (
    <div id='signin-form-container'>
      <div className='signin-container'>
        <Formik
          initialValues={initialValues}
          validationSchema={validationSchema}
          onSubmit={handleSubmit}
        >
          {({ isSubmitting }) => (
            <Form id='signin-form' className="text-center">
              <h2 className='signin-text'><b>RETURNING USER</b></h2>
              <h2 className="h4 mb-4 signin-text">Sign in</h2>
              <br></br>

              <Field 
                name='email' 
                id="defaultLoginFormEmail" 
                className="form-control mb-2 " 
                placeholder="Email" 
              />
              <ErrorMessage name='email' />

              <Field 
                name='password' 
                type='password'
                id="defaultLoginFormPassword" 
                className="form-control mb-4" 
                placeholder="Password" 
              />
              <ErrorMessage name='password' />

              <button 
                className="btn btn-outline-green btn-lg " 
                type="submit"
                disabled={isSubmitting}
              > 
                {isSubmitting ? 'Signing in...' : 'Sign me in!'}
              </button>
            </Form>
          )}
        </Formik>
      </div>
    </div>
  );
}

export default Signin;
