import React, { useState, useEffect } from 'react';
import Modal from 'react-modal'
import SlidingPane from 'react-sliding-pane'
import 'react-sliding-pane/dist/react-sliding-pane.css'
import ProductForm from '../farmerComponents/ProductForm'
import ProductCollection from '../farmerComponents/ProductCollection'

const FarmerContainer = ({ current_user, farmerProducts, addToFarmerProducts, removeProduct }) => {
  const [isPaneOpen, setIsPaneOpen] = useState(false);
  const [el, setEl] = useState(null);

  useEffect(() => {
    Modal.setAppElement(el);
  }, [el]);

  return (
    <div className='panel-icon sale-content' ref={ref => setEl(ref)}>
      <h2>You currently have <b>{farmerProducts.length}</b> product(s) on sale</h2>
      <button className='btn btn-lg btn-outline-white' onClick={() => setIsPaneOpen(true)}>Add a new product</button>
      <SlidingPane
        className='product-form-panel'
        overlayClassName='some-custom-overlay-class'
        isOpen={isPaneOpen}
        from='bottom'
        width='100%'
        height='100%'
        onRequestClose={() => setIsPaneOpen(false)}>
        <div id='panel-content'>
          <br />
          {<ProductForm current_user={current_user} farmerProducts={farmerProducts} addToFarmerProducts={addToFarmerProducts} />}
          <br />
        </div>
      </SlidingPane>
      <ProductCollection farmerProducts={farmerProducts} removeProduct={removeProduct} />
    </div>
  );
};

export default FarmerContainer;
