import React, { Component } from 'react';
import {Link} from 'react-router-dom'

import API from '../data/API'


class Signup extends Component {
    constructor(props) {
        super(props);

        this.state = {
            first_name: '',
            last_name: '',
            address: '',
            email: '',
            phone: '',
            password: '',
            user_type: '',
            showModal: false // Add this line to initialize showModal state property to false
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleUserType = this.handleUserType.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    showModalMessage() {
        this.setState({ showModal: true });
    }


    handleSubmit = (e) => {
        // API.signin(this.state)
        e.preventDefault()
        API.signup(this.state)
            .then(data => {
                if (data.error){
                    alert('not working!')
                }
                else {
                    // user is authentificated
                    this.props.signup(this.state.email, this.state.user_type) 
                    this.props.history.push('/signin')
                }
            })
    }

    handleChange = e => {
        e.preventDefault()
        this.setState({ [e.target.name]: e.target.value})
    }

    handleUserType = e => {
        e.preventDefault()
        this.setState({user_type: e.target.value}) 
    }

    render() {
        return (  
            <div id='signup-form-container'>
                <div id='signup-image'></div>
                <form id='signup-form' className="text-center ">
                    <h2>NEW USER</h2>
                    <br></br>
                    <fieldset>
                        <div>
                            <select className="form-control mb-4" id='select-user-type' onChange={this.handleUserType} value={this.state.value} >
                                <option value=''> -- Select an option -- </option>
                                <option value="farmer">I am a farmer</option>
                                <option value="customer">I am a Customer</option>
                            </select>
                        </div>
                    </fieldset>
                    <br></br>
                    <fieldset>
                        <input 
                            name='first_name' 
                            id="defaultSignup" 
                            onChange={this.handleChange} 
                            value={this.state.first_name} 
                            className="form-control mb-4" 
                            placeholder="first name" 
                        />
                        <input 
                            name='last_name' 
                            id="defaultSignup" 
                            onChange={this.handleChange} 
                            value={this.state.last_name} 
                            className="form-control mb-4" 
                            placeholder="last name" 
                        />
                        <input 
                            name='address' 
                            id="defaultSignup" 
                            onChange={this.handleChange} 
                            value={this.state.address} 
                            className="form-control mb-4" 
                            placeholder="address" 
                        />
                        <input 
                            name='email' 
                            id="defaultSignup" 
                            onChange={this.handleChange} 
                            value={this.state.email} 
                            className="form-control mb-4" 
                            placeholder="email" 
                        />
                        <input 
                            name='phone' 
                            id="defaultSignup" 
                            onChange={this.handleChange} 
                            value={this.state.phone} 
                            className="form-control mb-4" 
                            placeholder="phone" 
                        />
                        <input 
                            name='password' 
                            type='password'
                            id="defaultSignup" 
                            onChange={this.handleChange} 
                            value={this.state.password} 
                            className="form-control mb-4" 
                            placeholder="Password" 
                        />
                    </fieldset>
                    <button className="btn btn-block btn-outline-green btn-lg" id='signup-btn' onClick={(e) => this.handleSubmit(e)}  type="submit">Sign me up!</button>
                    <Link to='/signin'><p id='signup-question'>Already have an Account?</p></Link>
                </form>
                {this.state.errorMessage && 
                    <div className="modal fade show" tabIndex="-1" role="dialog" style={{display: 'block', backgroundColor: 'rgba(0,0,0,0.5)'}} aria-hidden="true">
                        <div className="modal-dialog modal-dialog-centered" role="document">
                            <div className="modal-content">
                                <div className="modal-header">
                                    <h5 className="modal-title">Error</h5>
                                    <button type="button" className="close" data-dismiss="modal" aria-label="Close" onClick={() => this.setState({errorMessage: ''})}>
                                        <span aria-hidden="true">&times;</span>
                                    </button>
                                </div>
                                <div className="modal-body">
                                    <p>{this.state.errorMessage}</p>
                                </div>
                            </div>
                        </div>
                    </div>
                }
            </div>
        );
    }


} 


export default Signup;
