import React, { Component } from 'react';
import './../App.css';

class ResourcesPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      article: null,
      articles: [
        {
          title: "The Benefits of Sustainable Agriculture",
          author: "Jane Smith",
          link: "https://www.agriculture.com/sustainable-agriculture/benefits-of-sustainable-agriculture"
        },
        {
          title: "10 Tips for Starting a Small Farm",
          author: "John Doe",
          link: "https://www.farmersalmanac.com/starting-a-small-farm-25903"
        },
        {
          title: "How Technology is Revolutionizing Agriculture",
          author: "Emily Jones",
          link: "https://www.cnbc.com/2021/04/02/how-technology-is-revolutionizing-agriculture.html"
        }
      ]
    }
  }

  getRandomArticle = () => {
    const randomIndex = Math.floor(Math.random() * this.state.articles.length);
    const randomArticle = this.state.articles[randomIndex];
    this.setState({ article: randomArticle });
  }

  render() {
    return (
      <div className="resources-container">
        <h2>Resources for Agriculture</h2>
        <div className="resources-content">
          {this.state.article ? (
            <div>
              <h3>{this.state.article.title}</h3>
              <p>By {this.state.article.author}</p>
              <a href={this.state.article.link}>Read more</a>
            </div>
          ) : (
            <p>Click the button below to generate a random agricultural article.</p>
          )}
          <button onClick={this.getRandomArticle}>Generate</button>
        </div>
      </div>
    );
  }
}

export default ResourcesPage;
