import React, { Component } from "react";
import { Link } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

import API from "../data/API";

class Signup extends Component {
  state = {
    email: "",
    first_name: "",
    last_name: "",
    address: "",
    phone: "",
    password: "",
    user_type: "",
  };

  handleSubmit = (e) => {
    // API.signin(this.state)
    e.preventDefault();
    API.signup(this.state).then((data) => {
      if (data.error) {
        toast.error("Not working!", {
          position: "top-center",
          autoClose: 3000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
        });
      } else {
        // user is authentificated
        this.props.signup(this.state.email, this.state.user_type);
        this.props.history.push("/signin");
      }
    });
  };

  handleChange = (e) => {
    e.preventDefault();
    this.setState({ [e.target.name]: e.target.value });
  };

  handleUserType = (e) => {
    e.preventDefault();
    this.setState({ user_type: e.target.value });
  };

  render() {
    return (
      // <!-- Default form login -->
      <div id="signup-form-container">
        <div id="signup-image"></div>
        <form id="signup-form" className="text-center ">
          <h2>NEW USER</h2>
          <br></br>
          <fieldset>
            <div>
              <select
                className="form-control mb-4"
                id="select-user-type"
                onChange={this.handleUserType}
                value={this.state.value}
              >
                <option value=""> -- Select an option -- </option>
                <option value="farmer">I am a farmer</option>
                <option value="customer">I am a Customer</option>
              </select>
            </div>
          </fieldset>

          <br></br>

          <fieldset>
            <input
              name="first_name"
              id="defaultSignup"
              onChange={this.handleChange}
              value={this.state.first_name}
              className="form-control mb-4"
              placeholder="first name"
            />

            <input
              name="last_name"
              id="defaultSignup"
              onChange={this.handleChange}
              value={this.state.last_name}
              className="form-control mb-4"
              placeholder="last name"
            />
            <input
              name="address"
              id="defaultSignup"
              onChange={this.handleChange}
              value={this.state.address}
              className="form-control mb-4"
              placeholder="address"
            />
            <input
              name="email"
              id="defaultSignup"
              onChange={this.handleChange}
              value={this.state.email}
              className="form-control mb-4"
              placeholder="email"
            />
            <input
              name="phone"
              id="defaultSignup"
              onChange={this.handleChange}
              value={this.state.phone}
              className="form-control mb-4"
              placeholder="phone"
            />
            <input
              name="password"
              type="password"
              id="defaultSignup"
              onChange={this.handleChange}
              value={this.state.password}
              className="form-control mb-4"
              placeholder="Password"
            />
          </fieldset>

          <Link to="/signin">
            <button
              className="btn btn-block btn-outline-green btn-lg"
              id="signup-btn"
              onClick={(e) => this.handleSubmit(e)}
              type="submit"
            >
              Sign me up!
            </button>
          </Link>
          <Link to="/signin">
            <p id="signup-question">Already have an Account?</p>
          </Link>
        </form>
      </div>
    );
  }
}

export default Signup;
