import React, { Component } from 'react';
import Modal from 'react-modal';
import BasketCard from './BasketCard';
import Checkout from '../components/Checkout';

// Bind modal to your appElement for accessibility
Modal.setAppElement('#root')

const customStyles = {
  content: {
    top: '50%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    marginRight: '-50%',
    transform: 'translate(-50%, -50%)'
  }
};

class Basket extends Component {
  constructor(props) {
    super(props);
    this.state = {
      modalIsOpen: false,
      customerBasket: []
    };
  }

  componentDidMount() {
    const customerBasket = JSON.parse(localStorage.getItem('customerBasket'));
    if (customerBasket) {
      this.setState({ customerBasket });
    }
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevState.customerBasket !== this.state.customerBasket) {
      localStorage.setItem('customerBasket', JSON.stringify(this.state.customerBasket));
    }
  }

  openModal = () => {
    this.setState({ modalIsOpen: true });
  };

  closeModal = () => {
    this.setState({ modalIsOpen: false });
  };

  deleteProduct = (id) => {
    const customerBasket = this.state.customerBasket.filter((product) => product.id !== id);
    this.setState({ customerBasket });
  };

  render() {
    const subtotal = this.state.customerBasket.map((el) => el.price).reduce((a, b) => a + b, 0);
    const deliveryFee = 200;
    const totalPrice = (subtotal + deliveryFee).toFixed(2);
    const product = this.state.customerBasket.map((product) => (
      <BasketCard key={product.id} product={product} deleteProduct={this.deleteProduct} basket_id={this.props.basket_id} />
    ));
    return (
      <div id="panel-content">
        {product}
        <hr />
        <div id="summary-content">
          <p>
            <i>Subtotal: ksh{subtotal}</i>
          </p>
          <p>
            <i>Delivery Fee: ksh{deliveryFee}</i>
          </p>
          <p>
            <b>Total: ksh{totalPrice}</b>
          </p>
        </div>
        <hr />
        <div id="checkout-btn">
          <button onClick={this.openModal} className="btn btn-md">
            Checkout
          </button>
          <Modal isOpen={this.state.modalIsOpen} onRequestClose={this.closeModal} onClick={this.closeModal} style={customStyles} contentLabel="Example Modal">
            <form>
              <Checkout />
            </form>
          </Modal>
        </div>
      </div>
    );
  }
}

export default Basket;
