import React, { Component, useState } from "react";
import ProductCard from "./ProductCard";

class ProductCollection extends Component {
  render() {
    const { allProducts, filterCategory, filterProducts } = this.props;
    const [currentPage, setCurrentPage] = useState(1);
    const [productsPerPage] = useState(3);

    const indexOfLastProduct = currentPage * productsPerPage;
    const indexOfFirstProduct = indexOfLastProduct - productsPerPage;
    const currentProducts =
      filterCategory === ""
        ? allProducts.slice(indexOfFirstProduct, indexOfLastProduct)
        : filterProducts(filterCategory).slice(
            indexOfFirstProduct,
            indexOfLastProduct
          );

    const product = currentProducts.map((product) => (
      <ProductCard
        key={product.id}
        id={product.id}
        product={product}
        addToBasket={this.props.addToBasket}
        basket_id={this.props.basket_id}
        customerBasket={this.props.customerBasket}
        history={this.props.history}
        current_user={this.props.current_user}
      />
    ));

    const paginate = (pageNumber) => setCurrentPage(pageNumber);
    const goBack = () => setCurrentPage(currentPage - 1);

    return (
      <div className="productCollection row align-items-stretch">
        {product}
        <div className="pagination">
          {indexOfLastProduct < allProducts.length && (
            <button
              style={{ backgroundColor: "white", color: "grey" }}
              className="next-btn"
              onClick={() => paginate(currentPage + 1)}
            >
              Load More
            </button>
          )}
          {currentPage > 1 && (
            <button
              style={{ backgroundColor: "white", color: "darkgrey" }}
              className="prev-btn"
              onClick={goBack}
            >
              &lt; Prev
            </button>
          )}
        </div>
      </div>
    );
  }
}

export default ProductCollection;
