import React, { useState } from 'react';
import Input from './Input';
import Button from './Button';
import MpesaForm from './MpesaForm';
import PaypalForm from './PaypalForm';

const Checkout = () => {
  const [paymentMethod, setPaymentMethod] = useState('card');

  const handlePaymentMethodChange = (e) => {
    setPaymentMethod(e.target.value);
  }

  return (
    <div className="checkout">
      <div className="checkout-container">
        <h3 className="heading-3">Payment Information</h3>
        <div className="form-group">
          <label htmlFor="payment-method-select">Payment Method</label>
          <select id="payment-method-select" className="form-control" value={paymentMethod} onChange={handlePaymentMethodChange}>
            <option value="card">Credit Card</option>
            <option value="mpesa">Mpesa</option>
            <option value="paypal">Paypal</option>
          </select>
        </div>
        {paymentMethod === 'card' && (
          <>
            <h3 className="heading-3">Credit card checkout</h3>
            <Input label="Cardholder's Name" type="text" name="name" />
            <Input label="Card Number" type="number" name="card_number" imgSrc="https://seeklogo.com/images/V/visa-logo-6F4057663D-seeklogo.com.png" />
            <div className="row">
              <div className="col">
                <Input label="Expiration Date" type="month" name="exp_date" />
              </div>
              <div className="col">
                <Input label="CVV" type="number" name="cvv" />
              </div>
            </div>
          </>
        )}
        {paymentMethod === 'mpesa' && <MpesaForm />}
        {paymentMethod === 'paypal' && <PaypalForm />}
        <Button text="Place order" />
      </div>
    </div>
  );
}

export default Checkout;
