import React, { Component } from 'react';

class Resource extends Component {

    constructor(props) {
        super(props);

        this.state = {
            resources: [],
            error: null,
            isLoading: false
        };
    }

    componentDidMount() {
        this.setState({ isLoading: true });

        // Your API call to fetch resources
        fetch('/api/resources')
            .then(response => response.json())
            .then(data => {
                this.setState({
                    resources: data,
                    isLoading: false
                })
            })
            .catch(error => this.setState({ error, isLoading: false }));
    }

    render() {
        const { resources, isLoading, error } = this.state;

        if (error) {
            return <div>Error: {error.message}</div>;
        }

        if (isLoading) {
            return <div>Loading...</div>;
        }

        return (
            <div>
                <h1>Resources</h1>
                <ul>
                    {resources.map(resource =>
                        <li key={resource.id}>
                            <h2>{resource.title}</h2>
                            <p>{resource.description}</p>
                            {resource.isFarmersOnly && this.props.user_type !== 'farmer' ? <p>This resource is only available to farmers.</p> : null}
                        </li>
                    )}
                </ul>
            </div>
        );
    }
}

export default Resource;
