import { Alert } from 'react-bootstrap';

const Signin = ({ signin }) => {

  const [showAlert, setShowAlert] = useState(false); // Add state to manage visibility of alert

  const handleSubmit = (values, { setSubmitting }) => {
    API.signin(values)
      .then(data => {
        setSubmitting(false);
        if (data.error){
          setShowAlert(true); // Display alert if email address not found
        }
        else {
          // user is authenticated! 
          signin(values.email, data.user, data.token);
        }
      });
  };

  return (
    <div>
      {showAlert && // Render alert only if showAlert is true
        <Alert variant="danger" onClose={() => setShowAlert(false)} dismissible>
          <Alert.Heading>Oops! Something went wrong</Alert.Heading>
          <p>Email address not found</p>
        </Alert>
      }
      <Formik
        initialValues={{ email: '', password: '' }}
        validate={values => {
          const errors = {};
          if (!values.email) {
            errors.email = 'Required';
          } else if (!isValidEmail(values.email)) {
            errors.email = 'Invalid email address';
          }
          if (!values.password) {
            errors.password = 'Required';
          }
          return errors;
        }}
        onSubmit={handleSubmit}
      >
        {({
          values,
          errors,
          touched,
          handleChange,
          handleBlur,
          handleSubmit,
          isSubmitting
        }) => (
          <Form>
            <FormGroup>
              <FormLabel>Email address</FormLabel>
              <FormControl
                type="email"
                name="email"
                placeholder="Enter email"
                onChange={handleChange}
                onBlur={handleBlur}
                value={values.email}
              />
              {errors.email && touched.email && <FormText>{errors.email}</FormText>}
            </FormGroup>
            <FormGroup>
              <FormLabel>Password</FormLabel>
              <FormControl
                type="password"
                name="password"
                placeholder="Enter password"
                onChange={handleChange}
                onBlur={handleBlur}
                value={values.password}
              />
              {errors.password && touched.password && <FormText>{errors.password}</FormText>}
            </FormGroup>
            <Button
              variant="primary"
              type="submit"
              disabled={isSubmitting}
            >
              Sign in
            </Button>
          </Form>
        )}
      </Formik>
    </div>
  );
};
