import React, { Component } from "react";
import { Link } from "react-router-dom";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";

import API from "../data/API";

class Signup extends Component {
  constructor(props) {
    super(props);

    this.state = {
      first_name: "",
      last_name: "",
      address: "",
      email: "",
      phone: "",
      password: "",
      user_type: "",
      showModal: false, // Add this line to initialize showModal state property to false
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleUserType = this.handleUserType.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  showModalMessage() {
    this.setState({ showModal: true });
  }

  handleSubmit = (e) => {
    // API.signin(this.state)
    e.preventDefault();
    API.signup(this.state).then((data) => {
      if (data.error) {
        // alert('not working!')
        this.showModalMessage();
      } else {
        // user is authentificated
        this.props.signup(this.state.email, this.state.user_type);
        this.props.history.push("/signin");
      }
    });
  };

  handleChange = (e) => {
    e.preventDefault();
    this.setState({ [e.target.name]: e.target.value });
  };

  handleUserType = (e) => {
    e.preventDefault();
    this.setState({ user_type: e.target.value });
  };

  render() {
    return (
      <div id="signup-form-container">
        <div id="signup-image"></div>
        <form id="signup-form" className="text-center ">
          <h2>NEW USER</h2>
          <br></br>
          <fieldset>
            <div>
              <select
                className="form-control mb-4"
                id="select-user-type"
                onChange={this.handleUserType}
                value={this.state.value}
              >
                <option value=""> -- Select an option -- </option>
                <option value="farmer">I am a farmer</option>
                <option value="customer">I am a Customer</option>
              </select>
            </div>
          </fieldset>
          <br></br>
          <fieldset>
            <input
              name="first_name"
              id="defaultSignup"
              onChange={this.handleChange}
              value={this.state.first_name}
              className="form-control mb-4"
              placeholder="first name"
            />
            <input
              name="last_name"
              id="defaultSignup"
              onChange={this.handleChange}
              value={this.state.last_name}
              className="form-control mb-4"
              placeholder="last name"
            />
            <input
              name="address"
              id="defaultSignup"
              onChange={this.handleChange}
              value={this.state.address}
              className="form-control mb-4"
              placeholder="address"
            />
            <input
              name="email"
              id="defaultSignup"
              onChange={this.handleChange}
              value={this.state.email}
              className="form-control mb-4"
              placeholder="email"
            />
            <input
              name="phone"
              id="defaultSignup"
              onChange={this.handleChange}
              value={this.state.phone}
              className="form-control mb-4"
              placeholder="phone"
            />
            <input
              name="password"
              type="password"
              id="defaultSignup"
              onChange={this.handleChange}
              value={this.state.password}
              className="form-control mb-4"
              placeholder="Password"
            />
          </fieldset>
          <button
            className="btn btn-block btn-outline-green btn-lg"
            id="signup-btn"
            onClick={(e) => this.handleSubmit(e)}
            type="submit"
          >
            Sign me up!
          </button>
          <Link to="/signin">
            <p id="signup-question">Already have an Account?</p>
          </Link>
          </form>
            <Modal show={this.state.showModal} onHide={() => this.setState({ showModal: false })}>
                <Modal.Header closeButton>
                    <Modal.Title>Error</Modal.Title>
                </Modal.Header>
                <Modal.Body>An error occurred during sign up. Please try again.</Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={() => this.setState({ showModal: false })}>
                        Close
                    </Button>
                </Modal.Footer>
            </Modal>
      </div>
    );
  }
}

export default Signup;
